SET WRITE_DELAY 0 MILLIS;
CREATE TEXT TABLE IF NOT EXISTS PUBLIC.KUNDE (ID_Kunde INTEGER PRIMARY KEY, Nachname VARCHAR(32), Strasse VARCHAR(32), Ort VARCHAR(32), PLZ VARCHAR(8), Anmerkung VARCHAR(256), Anrede VARCHAR(16), Kunde_seit INTEGER);
CREATE TEXT TABLE IF NOT EXISTS PUBLIC.KARTE (ID_Karte INTEGER, Kunde INTEGER, Kartentyp INTEGER, Jahr INTEGER);
CREATE TEXT TABLE IF NOT EXISTS PUBLIC.KARTE_TYP (ID_Kartentyp INTEGER, Kartentyp_Bezeichnug VARCHAR(32));
CREATE TEXT TABLE IF NOT EXISTS PUBLIC.AUFTRAG (ID_Auftrag INTEGER PRIMARY KEY, Kunde INTEGER, Datum DATE, Nr INTEGER, Betrag FLOAT, Neu VARCHAR(256), Notiz VARCHAR(256));
CREATE INDEX AUFTRAG_INDEX ON PUBLIC.AUFTRAG (Datum);
SET TABLE PUBLIC.KUNDE SOURCE 'tab_kunde2.csv;ignore_first=true;fs=\t;encoding=UTF-8';
SET TABLE PUBLIC.KARTE SOURCE 'tab_karte.csv;ignore_first=true;fs=\t;encoding=UTF-8';
SET TABLE PUBLIC.KARTE_TYP SOURCE 'tab_karte_typ.csv;ignore_first=true;fs=\t;encoding=UTF-8';
SET TABLE PUBLIC.AUFTRAG SOURCE 'tab_auftrag.csv;ignore_first=true;fs=\t;encoding=UTF-8';